package com.cbkj.rrh.main.account.model;import com.cbkj.rrh.others.utils.ThirdLoginAction.SocialLoginType;/** * @todo:第三方登录需要的参数 * @date:2015-10-5 下午6:35:42 * @author:hg_liuzl@163.com */public class ThirdLoginParam {		/**开放平台ID**/	public String openid;		/**开放平台用户ID**/	public String uid;		/**开放平台用户昵称**/	public String nickName;		/**开放平台用户性别**/	private int gender;		/**开放平台用户头像**/	public String headImgUrl;		/**开放平台令牌**/	public String accessToken;		/**开放平台用户描述，签名**/	public String signature;		/**用户登录类型*/	private int loginType = 0;		public int getGender() {		return gender;	}	public void setGender(String sex) {		this.gender = "男".equals(sex)?1:0;	}	public int getLoginType() {		return loginType;	}	public void setLoginType(SocialLoginType loginType) {		int type = 1;		if (SocialLoginType.QQLoginType == loginType) {			type = 1;		}else if (SocialLoginType.WeiboLoginType == loginType) {			type = 2;		}else if (SocialLoginType.WXLoginType == loginType) {			type = 3;		}		this.loginType = type;	}}